*/
  componentDidMount = () => {
    const weatherURL = `https://www.worldtradingdata.com/api/v1/stock?symbol=SNAP&api_token=juX9iGOEqRdzYSrtjQerwXJUI6KqtonttI4KkqdIhmyAIa0OK0gDTk9OLLLV`;
    fetch(weatherURL)
      .then(res => res.json())
      .then(data => {
        this.setState({ name: data.data[0].name, data: data }, () =>
          console.log(this.state)
        );
      });
  };
  // fetch info for the featuredStockSymbols

  render() {
    const { featuredStocks } = this.state;

    return <div className="markets-grid">stocks{featuredStocks}</div>;
  }
}
