import React, { Component } from "react";
import upward from "./upward-trend-flat.png";
import downward from "./downward-trend-flat.png";
import "./App.css";
import FeatureItem from "./FeatureItem/FeatureItem.js";
import ChartComponent from "./Chart/Chart.js";

export default class App extends Component {
  state = {
    stocks: []
  };
  componentDidMount = () => {
    const weatherURL = `https://www.worldtradingdata.com/api/v1/stock?symbol=MGROS.IS&api_token=juX9iGOEqRdzYSrtjQerwXJUI6KqtonttI4KkqdIhmyAIa0OK0gDTk9OLLLV`;
    fetch(weatherURL)
      .then(res => res.json())
      .then(data => {
        this.setState(
          {
            stocks: data.data
          },
          () => {
            console.log("sdhgsejhre");
            console.log(this.state);
            console.log(this.state.stocks[0].name);
            console.log(this.state.stocks);
          }
        );
      });
  };
  render() {
    const { stocks } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <FeatureItem
            src={upward}
            symbol="symbol"
            name={stocks[0]}
            price="5,097"
            day_change="+"
            change_pct="33.45"
            color="green"
          />
          <FeatureItem
            src={downward}
            symbol="symbol"
            name="Streams"
            price="47,403"
            day_change="-"
            change_pct="112.45"
            color="red"
          />
          <FeatureItem
            src={upward}
            symbol="symbol"
            name="Engagement Rate"
            price="25.81"
            day_change="+"
            change_pct="62.52"
            color="green"
          />
          <FeatureItem
            src={upward}
            symbol="symbol"
            name="Average Watch Time"
            price="45,4 min"
            day_change="+"
            change_pct="4.46"
            color="green"
          />
          <ChartComponent />
        </header>
      </div>
    );
  }
}
