import React, { Component } from "react";
import PropTypes from "prop-types";
import "./FeatureItem.css";

// the items inside the featured section
// clicking on them shows the details for that item below the featured area
export default class FeatureItem extends Component {
  state = {
    name: null,
    stocks: []
  };
  componentDidMount = () => {
    /* const weatherURL = `https://www.worldtradingdata.com/api/v1/stock?symbol=MGROS.IS&api_token=juX9iGOEqRdzYSrtjQerwXJUI6KqtonttI4KkqdIhmyAIa0OK0gDTk9OLLLV`;
    fetch(weatherURL)
      .then(res => res.json())
      .then(data => {
        this.setState(
          {
            name: data.data[0].name,
            data: data,
            change_pct: data.data[0].change_pct,
            day_change: data.data[0].day_change,
            price: data.data[0].price,
            symbol: data.data[0].currency,

            stocks: data.data
          },
          () => console.log(this.state)
        );
      });
      */
  };
  render() {
    const {
      name,
      src,
      color,
      change_pct,
      day_change,
      price,
      symbol
    } = this.props;

    return (
      <div className="featured-container__item" key={symbol}>
        <ul className="featured-stock">
          <li className="featured-stock__name">{name}</li>
          <li className="featured-stock__price">{price}</li>
          <div className="featured-stock__pct">
            <img src={src} className="featured-stock__icon" />
            <li className="featured-stock__change" style={{ color: color }}>
              {`${day_change} ${symbol} ${change_pct}%`}
            </li>
          </div>
        </ul>
      </div>
    );
  }
}
FeatureItem.propTypes = {
  stock: PropTypes.object.isRequired,
  color: PropTypes.string.isRequired,
  handleQuoteChange: PropTypes.func.isRequired
};
