import React, { Component } from "react";
import PropTypes from "prop-types";
import "./FeatureItem.css";

// the items inside the featured section
// clicking on them shows the details for that item below the featured area
const FeatureItem = ({ stock }) => (
  <div className="featured-container__item" key={stock.price.currency}>
    <ul className="featured-stock">
      <li className="featured-stock__name">{stock.name}</li>
      <li className="featured-stock__price">{stock.price.amount}</li>
      <div className="featured-stock__pct">
        <img src={stock.image.src} className="featured-stock__icon" />
        <li
          className="featured-stock__change"
          style={{ color: stock.image.color }}
        >
          {`${stock.change.value} ${symbol} ${stock.change.pct}%`}
        </li>
      </div>
    </ul>
  </div>
);

FeatureItem.propTypes = {
  stock: PropTypes.object.isRequired,
  color: PropTypes.string.isRequired,
  handleQuoteChange: PropTypes.func.isRequired
};
export default FeatureItem;
