import React, { Component } from "react";
import logo from "./logo.svg";
import upward from "./upward-trend-flat.png";
import downward from "./downward-trend-flat.png";
import "./App.css";
import FeatureItem from "./FeatureItem/FeatureItem.js";
import ChartComponent from "./Chart/Chart.js";
import { Link, BrowserRouter, Route } from "react-router-dom";
import Markets from "./Markets/Markets";

export default class App extends Component {
  state = {
    name: null,
    stocks: []
  };
  componentDidMount = () => {
    const weatherURL = `https://www.worldtradingdata.com/api/v1/stock?symbol=MGROS.IS&api_token=juX9iGOEqRdzYSrtjQerwXJUI6KqtonttI4KkqdIhmyAIa0OK0gDTk9OLLLV`;
    fetch(weatherURL)
      .then(res => res.json())
      .then(data => {
        this.setState(
          {
            name: data.data[0].name,
            data: data,
            change_pct: data.data[0].change_pct,
            day_change: data.data[0].day_change,
            price: data.data[0].price,
            symbol: data.data[0].currency,

            stocks: data.data
          },
          () => console.log(this.state)
        );
      });
  };
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <FeatureItem
            src={upward}
            symbol="symbol"
            name={stocks}
            price="5,097"
            day_change="+"
            change_pct="33.45"
            color="green"
          />
          <FeatureItem
            src={downward}
            symbol="symbol"
            name="Streams"
            price="47,403"
            day_change="-"
            change_pct="112.45"
            color="red"
          />
          <FeatureItem
            src={upward}
            symbol="symbol"
            name="Engagement Rate"
            price="25.81"
            day_change="+"
            change_pct="62.52"
            color="green"
          />
          <FeatureItem
            src={upward}
            symbol="symbol"
            name="Average Watch Time"
            price="45,4 min"
            day_change="+"
            change_pct="4.46"
            color="green"
          />
          <ChartComponent />
        </header>
      </div>
    );
  }
}
