import React, { useRef, useEffect } from "react";
import { Chart } from "chart.js";
import "./Chart.css";

const ChartComponent = ({ chartData }) => {
  let chart;
  const canvasElement = useRef(null);

  const onLoad = () => {
    initChart(canvasElement.current, chartData);
  };

  function initChart(element, chartData) {
    chart = new Chart(element, {
      // The type of chart we want to create
      type: "bar",
      data: {
        labels: chartData.labels,
        datasets: [
          {
            label: "# of Votes",
            data: chartData.prices,
            backgroundColor: [
              "rgba(255, 99, 132, 0.2)",
              "rgba(54, 162, 235, 0.2)",
              "rgba(255, 206, 86, 0.2)",
              "rgba(75, 192, 192, 0.2)"
            ],
            borderColor: [
              "rgba(255, 99, 132, 1)",
              "rgba(54, 162, 235, 1)",
              "rgba(255, 206, 86, 1)",
              "rgba(75, 192, 192, 1)"
            ],
            borderWidth: 1
          }
        ]
      },
      // Configuration options go here
      options: {}
    });
  }

  useEffect(() => {
    onLoad();
  }, [chart]);

  return (
    <canvas ref={canvasElement} id="myChart" width="400" height="400"></canvas>
  );
};

export default ChartComponent;
