import React, { Component } from "react";
import upward from "./upward-trend-flat.png";
import downward from "./downward-trend-flat.png";
import "./App.css";
import FeatureItem from "./FeatureItem/FeatureItem.js";
import ChartComponent from "./Chart/Chart.js";

export default class App extends Component {
  state = {
    stocks: [],
    stockArray: []
  };
  componentDidMount = () => {
    const weatherURL = `https://www.worldtradingdata.com/api/v1/stock?symbol=BIMAS.IS,CRFSA.IS,SOKM.IS,MGROS.IS&api_token=juX9iGOEqRdzYSrtjQerwXJUI6KqtonttI4KkqdIhmyAIa0OK0gDTk9OLLLV`;
    fetch(weatherURL)
      .then(res => res.json())
      .then(response => {
        const stocksArray = response.data;
        stocksArray.map(stock => ({
          name: stock.name,
          price: stock.price,
          change: {
            isIncreased: stock.day_change.includes("+"),
            value: stock.day_change
          }
        }));
        const stockArray = [];
        for (var i = 0; i < response.data.length; i++) {
          stockArray.push(response.data[i].price);
        }
        this.setState(
          {
            stocks: response.data,
            stockArray
          },
          () => {
            console.log(this.state);
          }
        );
      });
  };
  render() {
    const { stocks, stockArray } = this.state;

    console.log(stockArray);

    return (
      <div className="App">
        <header className="App-header">
          {stocks.map((stock, index) => (
            <FeatureItem
              key={index}
              src={stock.day_change.includes("-") ? downward : upward}
              symbol={stock.currency}
              name={stock.name}
              price={stock.price}
              day_change={stock.day_change}
              change_pct={stock.change_pct}
              color={stock.day_change.includes("-") ? "red" : "green"}
            />
          ))}

          <ChartComponent price={stockArray} />
        </header>
      </div>
    );
  }
}
