import React, { useRef, useEffect } from "react";
import { Chart } from "chart.js";
import "./Chart.css";
const generateChartOptions = (labels, prices) => ({
  // The type of chart we want to create
  type: "bar",
  data: {
    labels,
    datasets: [
      {
        label: "# of Votes",
        data: prices,
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)"
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)"
        ],
        borderWidth: 1
      }
    ]
  },
  // Configuration options go here
  options: {}
});
const useChart = function({ element, prices, labels }) {
  const chartOptions = generateChartOptions(prices, labels);
  return new Chart(element, chartOptions);
};

function ChartComponent({ prices, labels }) {
  const chart = useChart({ element, prices, labels });
